from django.db import models

class Profile(models.Model):
    logoname = models.CharField(max_length=3, default='BK')
    post = models.CharField(max_length=100, default='Full stack web developer')
    resume = models.FileField(upload_to='resume/')
    image = models.ImageField(upload_to='pic/', default='data:image/jpeg;base64,' )
    about = models.TextField(default = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud. Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt.')
    firstname = models.CharField(max_length=50, default='Bakhtawar')
    lastname = models.CharField(max_length=50, default='Khokhar')
    email = models.EmailField(default='bakhtawarkhokhar180@gmail.com')   
    phone = models.CharField(default='+91 0123456789') 
    location = models.TextField()   
    twitter = models.CharField(max_length=100)
    github = models.CharField(max_length=100)  
    threads = models.CharField(max_length=100)   
    linkedin = models.CharField(max_length=100)
    def __str__(self):
        return f"profile created for {self.logoname}"         

class Skills(models.Model):
    profile = models.ForeignKey(Profile, on_delete=models.CASCADE)
    skillname = models.CharField(max_length=100)
    skillicon = models.ImageField(upload_to='skilllogo/')

    def __str__(self):
        return f"learned {self.skillname} "
    
class Projects(models.Model):
    profile = models.ForeignKey(Profile, on_delete=models.CASCADE)
    img = models.ImageField(upload_to='projects/')
    title = models.CharField(max_length=100)
    description = models.TextField()
    link = models.URLField()

    def __str__(self):
        return f"displayed {self.title}" 
    








                            

